import React from 'react';

const TaskItem = ({ task, on }) => {
    return (
        <div className="task">
            <div className="label">{task.category}</div>
            <div className="text">{task.text}</div>
            {
                on ? <button className="delete">X</button>
                : null
            }
            <button
                className="delete"
            >
                X
            </button>
        </div>
    );
};

export default TaskItem;
